import React from 'react';

const TableHeader = () => {
    return (
        <thead>
            <tr>
                <th>Image</th>
                <th>Name</th>
                <th>Last stat created</th>
            </tr>
        </thead>
    );
}

const TableBody = props => {
    const rows = props.players.map((row, index) => {
        return (
            <tr key={index}>
                <td><img src={row.profileImage} alt=""/></td>
                <td>{row.username}</td>
                <td>{new Intl.DateTimeFormat('es-AR', {year: 'numeric', month: '2-digit',day: '2-digit', hour: '2-digit', minute: '2-digit', second: '2-digit'}).format(row.creation)}</td>
            </tr>
        );
    });

    return <tbody>{rows}</tbody>;
}

const Table = (props) => {
    const { players } = props;
    return (
        <table>
            <TableHeader />
            <TableBody players={players} />
        </table>
    );
}

export default Table;
