import React, { Component } from 'react';
import Table from './Table';
import Form from './Form';

class App extends Component {
    state = {
        players: [],
        interval: null
    };

    constructor() {
        this._fetchPlayers = this._fetchPlayers.bind(this);
    }

    componentDidMount() {
        const fetchPlayers = () => {
            const url = "https://en.wikipedia.org/w/api.php?action=opensearch&search=Seona+Dancing&format=json&origin=*&limit=1";
    
            fetch(url)
                .then(result => result.json())
                .then(result => { this.setState({ players: result }) });
        }
        this.setState({ ...this.state, interval: setInterval(fetchPlayers, 10000) });
    }
    
    componentWillUnmount() {
        clearInterval(this.state.interval);
    }

    /*handleSubmit = character => {
        this.setState({ characters: [...this.state.characters, character] });
        //TODO handleExport
    }*/

    render() {
        const { players } = this.state;
        
        return (
            <div className="container">
                <h1>Psh Exam</h1>
                <p>Top 10 players</p>
                <Table
                    players={players}
                />
                <h3>Export to csv</h3>
                {/* <Form handleSubmit={this.handleSubmit} /> */}
            </div>
        );
    }

}

export default App;
